
public class Loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			
			int count;
			//1.assignment----executes only once 
			//2.condition
			//3.statements or body of loop
			//4. increment or decrement
			
			for(count=5; count>=1;count--)// 6<=5 false count=count+1=2
			{
				System.out.println("hello welcome to java session");
			}
			
			System.out.println("end of loop");
		}

	}

